import React, { useContext } from "react";
import { UserContext } from "../data/context";

export const Login = () => {

    const { currentPage } = useContext(UserContext);


  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [token, setToken] = useState("");
  const [roomNum, setRoomNum] = useState("");
  const [userId,setUserId] = useState("");

  const userData = {
      "username" : email,
      "password": password,
  }

  const GetToken = () => {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: JSON.stringify(userData),
      redirect: "follow",
    };

    fetch("https://api.batborek.com/api/login2/", requestOptions)
      .then( async (response) => {
          const data = await response.json();
          setToken(data.token);
          setUserId(data.id);
          console.log('got token and id')
        })
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };

  return (
    <>
      <div className="myForm">
        <div className="item">
          {/* <h2>{t("Login.2")}</h2> */}
          <input
            onChange={(e) => setEmail(e.target.value)}
            value={email}
            type="text"
            id="email"
            name="email"
            className="text-input"
            placeholder="email"
          />

          <div className="passShow"></div>
          <input
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            id="password"
            name="password"
            value={password}
            // onKeyUp={validatePassword}
            className="password-input"
            placeholder="password"
          />

          <button
            type="button"
            className="forwardButton"
            disabled={false}
            onClick={()=>{
                GetToken();
                {token && setCurrentPage(2)}
            }}
          >
            <b>Login</b>

          </button>
          <h3>Dont have an account? <br />Sign Up</h3>
        </div>
      </div>
    </>
  );
};

export default Login;
